import React, { useEffect, useState } from "react";
import { useDispatch, connect } from "react-redux";
import { Button, Image, Tooltip } from "antd";
import { Card, Row, Col } from "react-bootstrap";

import { NavLink, withRouter } from "react-router-dom";
import "../../../Styling/chaletcard.css";
import { deleteChalet } from "../../../redux/actions/chaletActionCreator";
import DeleteModal from "../../../Components/modal/deleteModal";
import EditChaletModal from "../../../Components/modal/editChaletModal";

function ChaletCard({ chalet, match, userChalet }) {
  const dispatch = useDispatch();
  const [status, setStatus] = useState("");
  const [deleteModalShow, setDeleteModalShow] = React.useState(false);
  const [editModalShow, setEditModalShow] = React.useState(false);
  const handleDelete = () => {
    if (chalet) dispatch(deleteChalet(chalet.id));
    else {
      dispatch(deleteChalet(userChalet.id));
    }
    setDeleteModalShow(false);
  };
  useEffect(() => {
    if (userChalet && userChalet.verification === "verified") {
      setStatus("check-circle text-success");
    } else if (userChalet && userChalet.verification === "waiting") {
      setStatus("stopwatch text-warning medium");
    } else if (userChalet && userChalet.verification === "not_verified") {
      setStatus("ban text-danger");
    }
  }, [userChalet]);
  // console.log(userChalet, chalet, match);
  return (
    <Card
      className={`mt-5 shadow-card ${
        match && match.url.slice(1, 8) === "profile"
          ? "Card-prof-height"
          : "Card-view-height"
      }`}
    >
      {chalet !== undefined && (
        <>
          {chalet && chalet.cover !== null && (
            <Card.Img
              variant="top"
              src={`${chalet && chalet.cover}`}
              className="imgCard"
            />
          )}
          {chalet && !chalet.cover && (
            <Image
              // width={200}
              height={127}
              src="error"
              fallback="data:image/png;base64,"
            />
          )}
          <Card.Body className="p-2">
            <Row>
              <Col>
                <h3>{chalet && chalet.address}</h3>
                {/* <Button
                  variant="outline-warning"
                  className="edit"
                  roundedCircle
                  onClick={() => setEditModalShow(true)}
                >
                  <i className="fas fa-edit text-white editIcon"></i>
                </Button>
                <EditChaletModal
                  show={editModalShow}
                  onHide={() => setEditModalShow(false)}
                /> */}
              </Col>
            </Row>
            <h4>
              $ {chalet && chalet.fees}
              {/* <Badge className="badge p-3 ml-3">For Sell</Badge> */}
            </h4>
            <div className="features d-flex">
              <>
                <div className="h6 ml-2">
                  <img
                    src="/images/wifi.png"
                    className="featureImg"
                    alt="wifi"
                  />
                  {chalet && chalet.feature[0]}
                </div>
                <div className="h6">
                  <img
                    src="/images/garden.png"
                    className="featureImg"
                    alt="garden"
                  />
                  {chalet && chalet.feature[1]}
                </div>
                <div className="h6 air">
                  <img
                    src="/images/air condition.png"
                    className="featureImg"
                    alt="air condition"
                  />
                  {chalet && chalet.feature[2]}
                </div>
              </>
            </div>
            <Card.Title>ABOUT</Card.Title>
            <Card.Text className="coloor">
              {chalet && chalet.description}
            </Card.Text>
          </Card.Body>
          <div className="footerCard">
            <NavLink
              variant="primary"
              className="active m-3 pl-4 pr-4 p-3 view text-white"
              to={{
                pathname: `/viewchalet/${chalet && chalet.id}`,
                // aboutProps: {
                //   state: chalet,
                // },
              }}
            >
              view
            </NavLink>
          </div>
        </>
      )}
      {userChalet !== undefined && (
        <>
          {userChalet && userChalet.cover !== null && (
            <Card.Img
              variant="top"
              src={`${userChalet && userChalet.cover}`}
              className="imgCard"
            />
          )}
          {userChalet && !userChalet.cover && (
            <Image
              // width={200}
              height={149}
              src="error"
              fallback="data:image/png;base64,"
            />
          )}
          <Card.Body className="p-2">
            {/* <Row>
              <Col> */}
            <h3 className="d-inline-block">
              {userChalet && userChalet.address}
            </h3>

            {/* </Col>
            </Row> */}
            <h4>
              $ {userChalet && userChalet.fees}
              {/* <Badge className="badge p-3 ml-3">For Sell</Badge> */}
            </h4>
            <div className="features d-flex">
              <>
                <div className="h6 ml-2">
                  <img
                    src="/images/wifi.png"
                    className="featureImg"
                    alt="wifi"
                  />
                  {userChalet && userChalet.feature && userChalet.feature[0]}
                </div>
                <div className="h6">
                  <img
                    src="/images/garden.png"
                    className="featureImg"
                    alt="garden"
                  />
                  {userChalet && userChalet.feature && userChalet.feature[1]}
                </div>
                <div className="h6 air">
                  <img
                    src="/images/air condition.png"
                    className="featureImg"
                    alt="air condition"
                  />
                  {userChalet && userChalet.feature && userChalet.feature[2]}
                </div>
              </>
            </div>
            <Card.Title>ABOUT</Card.Title>
            <Card.Text className="coloor">
              {userChalet && userChalet.description}
            </Card.Text>
          </Card.Body>

          <div className="footerCard">
            <NavLink
              variant="primary"
              className="active m-3 pl-4 pr-4 p-3 view text-white"
              to={{
                pathname: `/viewchalet/${userChalet && userChalet.id}`,
                // aboutProps: {
                //   state: chalet,
                // },
              }}
            >
              view
            </NavLink>
            <Tooltip
              title={
                userChalet.verification === "waiting" &&
                userChalet.verification !== "verified"
                  ? "your chalet is in the waiting List to verify it"
                  : userChalet.verification === "not_verified"
                  ? "you need to verify your chalet"
                  : "This Chalet is verified"
              }
              color={
                userChalet.verification === "waiting" &&
                userChalet.verification !== "verified"
                  ? "gold"
                  : userChalet.verification === "not_verified"
                  ? "red"
                  : "green"
              }
              // key={color}
            >
              <Button className="border-none status">
                {" "}
                <i
                  className={`fas fa-${status} status`}
                  style={{ fontSize: "medium" }}
                ></i>
              </Button>
            </Tooltip>

            <Button
              // type="primary"
              className="edit-chalet"
              roundedCircle
              shape="circle"
              onClick={() => setEditModalShow(true)}
            >
              <i class="fas fa-pen text-primary editIcon"></i>
              {/* <i className="fas fa-edit text-primary editIcon"></i> */}
            </Button>
            <EditChaletModal
              userChalet={userChalet}
              show={editModalShow}
              onHide={() => setEditModalShow(false)}
            />
            <Button
              className="border-0 bg-transparent btn-trash"
              onClick={() => setDeleteModalShow(true)}
            >
              <i class="fas fa-trash-alt text-primary"></i>
            </Button>

            <DeleteModal
              show={deleteModalShow}
              onHide={() => setDeleteModalShow(false)}
              handleDelete={handleDelete}
            />
          </div>
        </>
      )}
    </Card>
  );
}
const mapStateToProps = (reduxState) => {
  return {
    // userChalet: reduxState.Chalets.currentUserChalets,
  };
};
export default connect(mapStateToProps)(withRouter(ChaletCard));
